mapper.select:
    
    	select count(1)fwcs from t_sqrxx 
		where 1=1
		
            AND CXBS = #{cxbs}
		
		
            AND CZDW = #{czdw}
		
		AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}

	select count(1)totalRows,count(cxcs)cxcs from t_bcxrxx bc
	where bc.SQR_ID IN(
		select ID from t_sqrxx
		where 1=1
		
            AND CXBS = #{cxbs}
		
		
            AND CZDW = #{czdw}
		
		AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}
	)

	select count(1)totalRows,count(cxcs)cxcgcs from t_bcxrxx bc
	where  bc.SFZF='0' and bc.SQR_ID IN(
		select ID from t_sqrxx
		where 1=1
		
            AND CXBS = #{cxbs}
		
		
            AND CZDW = #{czdw}
		
		AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}
	)
