mapper.select:
    
    	select count(1)fwcs from t_sqrxx 
		where 1=1
		
            AND CXBS = #{cxbs}
		
		
            AND CZDW = #{czdw}
		
		AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}

	select count(1)totalRows,SUM(cxcs)cxcs from t_bcxrxx bc
	where bc.SQR_ID IN(
		select ID from t_sqrxx
		where 1=1
	
            AND CXBS = #{cxbs}
	
	
            AND CZDW = #{czdw}
	
		AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}
	)

	select count(1)totalRows,SUM(cxcs)cxcgcs from t_bcxrxx bc
	where  bc.SFZF='0' and bc.SQR_ID IN(
		select ID from t_sqrxx
		where 1=1
	
            AND CXBS = #{cxbs}
	
	
            AND CZDW = #{czdw}
	
		AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}
	)

	select czr,sum(sfdy) dycs,sum(cxcs) cxcs
	FROM(
		select bc.SFDY, bc.SFZF, bc.XGRQ, bc.RKLX, bc.CXCS, bc.BCXRQ, sq.CZDW, sq.CZR, sq.CXBS
		FROM
	    	t_bcxrxx bc left join t_sqrxx sq on sq.ID=bc.SQR_ID
		where bc.SFZF='0' 
		
            AND sq.CXBS = #{cxbs}
		
			AND bc.BCXRQ BETWEEN #{startDateStr} AND #{endDateStr}
	)t group by czr

SELECT
    a.czdw,a.fwcs,b.cxcs,c.cxcgcs,
    (SELECT ORG_NAME FROM  audit_organization o WHERE a.czdw=o.org_id ) dwmc
FROM
    (
        /**服务次数*/
        SELECT  COUNT(1)fwcs,  czdw
        FROM
            t_sqrxx
        WHERE
            CXBS = #{cxbs}
        AND CXRQ BETWEEN #{startDateStr} AND #{endDateStr}
        AND CZDW IN(
        	${czdwSplitByComma}
        )
        GROUP BY  czdw
    )  a LEFT JOIN
    (
        /**查询次数*/
        SELECT sq.CZDW,  COUNT(1)totalRows, SUM(bc.cxcs)cxcs
        FROM
            t_bcxrxx bc
        LEFT JOIN t_sqrxx sq
        ON sq.ID=bc.SQR_ID
        WHERE
            sq.CXBS = #{cxbs}
        AND bc.BCXRQ BETWEEN #{startDateStr} AND #{endDateStr}
        AND sq.CZDW IN(
        	${czdwSplitByComma}
        )
        GROUP BY sq.CZDW
    )
    b
ON  a.czdw=b.czdw
LEFT JOIN
    (
        /**查询成功次数*/
        SELECT  sq.CZDW, COUNT(1)totalRows,  SUM(bc.cxcs)cxcgcs
        FROM
            t_bcxrxx bc
        LEFT JOIN t_sqrxx sq
        ON sq.ID=bc.SQR_ID
        WHERE
            sq.CXBS = #{cxbs}
        AND bc.SFZF='0'
        AND bc.BCXRQ BETWEEN #{startDateStr} AND #{endDateStr}
        AND sq.CZDW IN(
        	${czdwSplitByComma}
        )
        GROUP BY sq.CZDW
    )c
ON a.czdw=c.czdw
